import Link from "next/link";
import Head from "next/head";
import Router from "next/router";
import Nprogress from "nprogress";

Router.onRouteChangeStart

export default ({children, title}) => (
<div className="root">
    <Head>
        <title>Next-Portfolio</title>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/ngprogress/1.1.3/ngprogress.min.js" />
    </Head>
    <header>
        <Link href="/"><a>Home</a></Link>
        <Link href="/about"><a>About</a></Link>
        <Link href="/hireme"><a>Hire Me</a></Link>
    </header>
    <h1>{title}</h1>
    {children}

    <footer>ShahriarEmpyreal&copy;{new Date().getFullYear()}</footer>
<style jsx>{`
    .root {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
    }
    header {
        width: 100%;
        display: flex;
        justify-content: space-around;
        padding: 1em;
        font-size: 1.2rem;
        background: indigo;
    }
    header a {
        color: darkgrey;
        text-decoration: none;
    }
    header a:hover {
        font-weight: bold;
        color: lightgrey;
    }
    footer {
        padding: 1em;
    }
`}</style>
<style global jsx>{`
    body{
        margin: 0;
        font-size: 110%;
        background: #f0f0f0;
    }
`}</style>
</div>
);
